        def Organizer():
                dir = file
                files_organizer = os.listdir(dir)

                for x in files_organizer:
                        type = os.path.splitext(x)
                        newtype =  str(type [1:])
                        
                        print(newtype)
                
                        if os.path.exists(dir+'/'+newtype):
                                shutil.move(dir+'/'+x, dir+'/'+newtype)

                        else: 
                                os.mkdir(dir+'/'+newtype)
                                shutil.move(dir+'/'+x, dir+'/'+newtype)
                                print('is done! ;)')
        

